        <label>Date</label>
        <input type='date' min='2023-01-01' max='2023-12-31' value={firstInput.firstDate}  onChange={DateChange} />
        </div>
        </div>
        <div className='new-expense__actions'>
        <button type='submit'> Add expense</button>
        </div>
    </form>
    )
