import './ExpenseForm.css'

import React , {useState} from 'react'

export default function ExpenseForm(){
   const [firstInput, secondInput] = useState({
    firstTitle :"",
    firstDate:'',
    firstAmount:''
   })


   const TitleChange = function(event){
       secondInput({
        ...firstInput,
        firstTitle:event.target.value

   })
}
   const DateChange = function(event){
    secondInput({
     ...firstInput,
     firstDate:event.target.value

})
   }

const AmountChange = function(event){
    secondInput({
     ...firstInput,
     firstAmount:event.target.value

})
}


    return (
        <form>
        <div className='new-expense__controls'>
        <div className='new-expense__control'>
        <label>Title</label>
        <input type='text' onChange={TitleChange} />
        </div>
        <div className='new-expense__control'>
        <label>Amount</label>
        <input type='number' min='0.01' step='0.01' onChange={AmountChange} />
        </div>
        <div className='new-expense__control'>
        <label>Date</label>
        <input type='date' min='2023-01-01' max='2023-12-31' onChange={DateChange} />
        </div>
        </div>
        <div className='new-expense__actions'>
        <button type='submit'> Add expense</button>
        </div>
    </form>
    )
}
